from statistics import mean
import re
import base64

def otp(data, key):
    out = [(lambda a, b : a ^ b)(*l) for l in zip(data, key)]
    return bytes(out)

def repeatingxor(b, key):   
    k = (key * len(b))[0:len(b)]
    return otp(b, k)

